import React from 'react';
import ReactDOM from 'react-dom';
// JS

function TitleJS(){
  return (
    React.createElement('div', {className: 'book'}, 'The Title')
  );
}

function AuthorJS() {
  return (
    React.createElement('div', {className: 'author'}, 'The Author')
  );
}

function RatingJS() {
  return (
    React.createElement('li', {className: 'rating'}, '5 stars')
  );
}

function ISBNJS() {
  return (
    React.createElement('li', {className:'isbn'}, '12-345678-910')
  );
}

function StatsJS() {
  return (
    React.createElement('ul', {className: 'stats'},
                        React.createElement(RatingJS, null), React.createElement(ISBNJS, null))
  );
}

// JSX
function Title(){
  return (
    <div className = 'book'>
      The Title
    </div>
  );
}

function Author() {
  return (
    <div className = 'author'>
      The Author
    </div>
  );
}

function Rating() {
  return (
    <li className = 'rating'>
      5 stars
    </li>
  );
}

function ISBN() {
  return (
    <li className = 'isbn'>
      12-345678-910
    </li>
  );
}

function Stats() {
  return (
    <ul className = 'stats'>
      <Rating/>
      <ISBN/>
    </ul>
  );
}

function JSXspace() {
  return (
    <div>
      &nbsp;Non-breaking
      &nbsp;Spaces&nbsp;
    </div>
  );
}

function JSXlinespace() {
  return (
    <div>
      Line1
      {' '}
      Line2
    </div>
  );
}

function Greeting() {
  let username = "root";
  //let username = undefined;
  //let username = null;
  //let username = false;
  return (
    <div>
      {typeof username == 'string' ? 'Hello, ' + username: 'Not logged in.'}
    </div>
  );
}

function MyThing() {
  return (
    <div className = 'book'>
      <TitleJS/>
      <AuthorJS/>
      <StatsJS/>
      <JSXspace/>
      <JSXlinespace/>
      <Greeting/>
    </div>
  )
}

ReactDOM.render(
  <MyThing/>,
  document.querySelector('#root')
);
